"// Mapping logic to generate the Surname according to the following convention.
// B	Boele
// BP	Boele – De Vries
// P	Vries
// PB	Vries – van den Boele
function generateSurname() {
    let middleName = Person.Name.FamilyNamePrefix;
    let lastName = Person.Name.FamilyName;
    let middleNamePartner = Person.Name.FamilyNamePartnerPrefix;
    let lastNamePartner = Person.Name.FamilyNamePartner;
    let convention = Person.Name.Convention;

    let surName = '';
    switch (convention) {
        case "BP":
            surName = surName + lastName;

            surName = surName + ' - ';
            if (typeof middleNamePartner !== 'undefined' && middleNamePartner) { surName = surName + middleNamePartner + ' ' }
            surName = surName + lastNamePartner;
            break;
        case "PB":
            surName = surName + lastNamePartner;
            surName = surName + ' - ';
            if (typeof middleName !== 'undefined' && middleName) { surName = surName + middleName + ' ' }
            surName = surName + lastName;
            break;
        case "P":
            surName = surName + lastNamePartner;
            break;
        case "B":
        default:
            surName = surName + lastName;
            break;
    }
    // Trim spaces at start and end
    surName = surName.trim();

    // 20260210KM: undocumented, leave in comment, use when needed. --> according to database specs nvarchar(50)
    // Shorten string to maxAttributeLength 
    const maxAttributeLength = 50;
    surName = surName.substring(0, maxAttributeLength);

    return surName;
}

generateSurname();"

"function getMiddleName() {
    let middleName = Person.Name.FamilyNamePrefix;
    let middleNamePartner = Person.Name.FamilyNamePartnerPrefix;
    let convention = Person.Name.Convention;
    let retVal = '';

    switch (convention) {   
        case "PB":
        case "P":
            if (typeof middleNamePartner !== 'undefined' && middleNamePartner) { retVal = middleNamePartner }
            break;
        case "B":
        case "BP":
        default:
            if (typeof middleName !== 'undefined' && middleName) { retVal = middleName }
            break;
    }
    return retVal;
}

getMiddleName();"
